class Solution:
    def firstMissingPositive(self, nums: List[int]) -> int:
        n = len(nums)

        i = 0
        while i < n:
            while 0 < nums[i] <= n and nums[i] != i + 1 and nums[nums[i] - 1] != nums[i]:
                # termination conditions:
                # 1. current number is less than zero or greater than n, leave it and check the next position
                # 2. current number is the right number
                # 3. the position corresponds to current number has already contain the right number
                # Otherwise, exchange between current position and the position corresponds to current number

                tmp = nums[i]
                nums[i], nums[tmp - 1] = nums[tmp - 1], nums[i]
            i += 1

        for i in range(n):
            if nums[i] != i + 1:
                return i + 1
        return n + 1
