/**
 * @param {number[]} nums
 * @return {number}
 */
var firstMissingPositive = function(nums) {
    // In this approach we will loop through array and if we find element is in
    // range (1 to n) then swap the elemnt with index equal to its value-1.
    // example--> if 2 is element then swap 2 with index 2-1 or(1)'s value . Notice that if 
    //another duplicate 2 is already present at index then no need to swap.
    let n=nums.length;
    let index;
    let element;
    let temp;
    // run a loop through the given array
    for(let i=0;i<n;i++){
        // if element is from 1 to n.
        if(nums[i]>=1 && nums[i]<=n){
            element=nums[i];
            index=element-1;
            // if element is not at correct index swap it.
            if(nums[index]!=element){
                   temp=nums[index];
                   nums[index]=element;
                   nums[i]=temp;
                   i--;
            }
        }
    }

    // run a loop and find if index i has value=i+1. if not return i+1(because it will
    // be the first missing positive number)
    for(let i=0;i<n;i++){
        if(nums[i]!=i+1){
            return i+1;
        }
    }

    // if all elements are found till length of array then return length+1.
    return n+1;
};
