class Solution {
     public static int[] mergesort(int[] arr){
        if(arr.length<=1){
            return arr;
        }
        int mid = (arr.length)/2;
        int[] arr1 = Arrays.copyOfRange(arr,0,mid);
        arr1 = mergesort(arr1);
        int[] arr2 = Arrays.copyOfRange(arr,mid,arr.length);
        arr2 = mergesort(arr2);

        int[] ans = new int[arr.length];
        int i=0,j=0;
        while( i<arr1.length && j<arr2.length){
            if(arr1[i] > arr2[j]){
                ans[i+j]=arr2[j];
                j++;
            }else {
                ans[i+j]=arr1[i];
                i++;
            }
        }
        if(i==arr1.length){
            for(;j<arr2.length;j++){
                ans[arr1.length+j]=arr2[j];
            }

        }else if (j==arr2.length){
            for(;i<arr1.length;i++){
                ans[arr2.length+i]=arr1[i];
            }
        }
        return ans;
    }
    public int firstMissingPositive(int[] nums) {
        nums=mergesort(nums);
        int ans=1;
        int i=0;
        while(i<nums.length && nums[i]!=1){
            i++;
        }
        if(i==nums.length){
            return ans;
        }
        while(i<nums.length-1 && nums[i]>=nums[i+1]-1){
            i++;
        }
        return nums[i]+1;
    }
}

class Solution {
    public int firstMissingPositive(int[] nums) {

        if(nums.length==1){
            if(nums[0]==1){
                return 2;
            }
            else{
                return 1;
            }
        }
        Arrays.sort(nums);
        List<Integer> ans=new ArrayList<>();    
        HashSet<Integer>uni=new HashSet<>();

        for(int i:nums){
            uni.add(i);
        }

        for(int i=1;i<=nums.length+1;i++){
            if(uni.contains(i)){
                continue;
            }
            else{
                return i;
            }
        }


        return 0;
    }
}

class Solution {
    public int firstMissingPositive(int[] nums) {
        Map<Integer, Integer> m= new HashMap<>();
        Arrays.sort(nums);

        for(int n: nums){
            if(n>0){
                 m.put(n,m.getOrDefault(m, 0)+1);
            }
        }
        for(int i=1;i<=m.size();i++){
            if(!m.containsKey(i)){
                return i;

            }
        }
        return m.size()+1;

    }
}




