class Solution 
{
public:
    // This function tests whether the string contains any opeartor in index between start and end
    bool has_operator(string s,int start,int end)
    {
        for(int i=start;i<=end;i++)
        {
            if(s[i]=='+' || s[i]=='-' || s[i]=='*')
            {
                return true;
            }
        }
        return false;
    }
    // This is the helper function to generate all the valid results 
    vector<int> helper(string &s,int start,int end)
    {
        // Base case
        if(!has_operator(s,start,end))
        {
            return {stoi(s.substr(start,end+1))};
        }
        // Recursive case
        vector<int>ans;
        for(int i=start;i<=end;i++)
        {
            if(s[i]=='+' || s[i]=='-' || s[i]=='*')
            {   // If we have found an operator then get the result vector from the left side and from the right side of the string and combine each result of left side to each result of right side and store the results in the answer vector
                vector<int>first=helper(s,start,i-1);
                vector<int>second=helper(s,i+1,end);
                for(auto x:first)
                {
                    for(auto y:second)
                    {
                        int res;
                        if(s[i]=='+')
                        {
                            res=x+y;
                        }
                        else if(s[i]=='-')
                        {
                            res=x-y;
                        }
                        else if(s[i]=='*')
                        {
                            res=x*y;
                        }
                        ans.push_back(res);
                    }
                }
            }
        }
        return ans;
    }
    vector<int> diffWaysToCompute(string expression) 
    {
        return helper(expression,0,expression.length()-1);
    }
};
