class Solution {
private:
    map<string, vector<int>>M;

public:
    vector<string> help(string &s)
    {
        vector<string> V;
        string t;

        for(auto c:s)
        {
            if(c == '+' or c == '-' or c == '*')
            {
                V.push_back(t);
                t = "";
                V.push_back(string(1, c));
            }
            else
                t += c;
        }

        if(t.size())
            V.push_back(t);

        return V;
    }

    vector<int> solve(vector<string>&s, int i, int j)
    {
        if(i == j)
            return vector<int>(1, stoi(s[i]));

        string key = to_string(i) + "#" + to_string(j);

        if(M.find(key) != M.end())
            return M[key];

        vector<int> ansInter;
        for(int k=i+1; k<j; k+=2)
        {
            vector<int> left = solve(s, i, k-1);
            vector<int> right = solve(s, k+1, j);

            for(auto l:left)
            {
                for(auto r:right)
                {
                    int ans;
                    if(s[k] == "+")
                        ans = l + r;

                    else if(s[k] == "-")
                        ans = l - r;
                    else
                        ans = l * r; 

                    ansInter.push_back(ans);
                }
            }

        }

        return M[key] = ansInter;
    }

    vector<int> diffWaysToCompute(string s) {

        vector<string> V = help(s);
        return solve(V, 0, V.size()-1);
    }
};
