class Solution {

    private boolean isOperator (char operator) {

        return operator == '+' || operator == '-' || operator == '*' || operator == '/';
    }

    private int calculateValue (int a, int b, char operator) {

        if (operator == '+') {
            return a + b;
        }
        else if (operator == '-') {
            return a - b;
        }
        else if (operator == '*') {
            return a * b;
        }
        else {
            return a / b;
        }
    }

    public List<Integer> diffWaysToCompute(String expression) {

        List<Integer> answer = new ArrayList<> ();

        for (int i = 0; i < expression.length (); i++) {
            char operator = expression.charAt (i);
            if (isOperator (operator)) {
                List<Integer> list1 = diffWaysToCompute (expression.substring (0, i));
                List<Integer> list2 = diffWaysToCompute (expression.substring (i + 1));

                for (int j = 0; j < list1.size (); j++) {
                    for (int k = 0; k < list2.size (); k++) {
                        answer.add (calculateValue (list1.get (j), list2.get (k), operator));
                    }
                }
            }
        }

        if (answer.size () == 0) {
            answer.add (Integer.valueOf (expression));
            return answer;
        }

        return answer;
    }
}
