class Solution:
    def diffWaysToCompute(self, s: str) -> List[int]:
        def solve(start, end):
            out = []
			# if the range is number - return number (as expressions look like 'num operation num', so start and end are nums)
            if start == end:
                return [ops[start]]

			# split the list by each operation into left and right expressions
            for k in range(start + 1, end, 2): # operation is +2 away from another operation
                left = solve(start, k - 1) # up to operation char
                right = solve(k + 1, end) # right next after the operation char

				# product of left results 'operation' right results
                for l in left:
                    for r in right:
                        if ops[k] == "+":
                            out.append(l + r)
                        elif ops[k] == "-":
                            out.append(l - r)
                        else:
                            out.append(l * r)

            return out

