        ops = []
        for c in s:
            if not c.isnumeric():
                ops.append(c)
            else:
                if not ops or ops and type(ops[-1]) == type(""):
                    ops.append(0)
                ops[-1] *= 10
                ops[-1] += int(c)

        N = len(ops)
        return solve(0, N - 1)
