class Solution {
public:
    vector<int> diffWaysToCompute(string expression) {
        // idea: use the operator to tear the expression apart and recursively do this. 
        vector<int> res;
        for (int i = 0; i < expression.size(); i++) {
            if (expression[i] == '+' || expression[i] =='-' || expression[i] == '*') {
                // tear the expression apart -> left expression and right expression
                string left = expression.substr(0, i);
                string right = expression.substr(i+1, expression.size()-i-1);
                // cout << "left: " << left << ", operator: " << expression[i] << ", right: " << right << endl;
                vector<int> l = diffWaysToCompute(left);
                vector<int> r = diffWaysToCompute(right);

                // merge and get the combination of answers by two for loops according to the operator
                for (int j = 0; j < l.size(); j++) {
                    for (int k = 0; k < r.size(); k++) {
                        if (expression[i] == '+')
                            res.push_back(l[j] + r[k]);
                        if (expression[i] == '-')
                            res.push_back(l[j] - r[k]);
                        if (expression[i] == '*')
                            res.push_back(l[j] * r[k]);
                    }
                }
            }
        }

		// the expression only contains the number, otherwise, the res definitely contains at least a number from the above for loop.
        if (res.empty()){
            int sum = stoi(expression);
            res.push_back(sum);
        }
        return res;
    }

};
