        for(int i=0;i<n;i++)
        {
            char c=expression[i];
            if(c=='+'||c=='-'||c=='*')
            {
                vector<int> L=diffWaysToCompute(expression.substr(0,i)),R=diffWaysToCompute(expression.substr(i+1,n-i-1));
                for(int l:L)
                    for(int r:R)
                        switch(c)
                        {
                            case '+':ans.push_back(l+r);break;
                            case '-':ans.push_back(l-r);break;  
                            case '*':ans.push_back(l*r);break;
