class Solution {
public:
    vector<int> diffWaysToCompute(string expression) {
        int n=expression.size();
        vector<int>num,op;
        for(int i=0;i<n;i++)
        {
            int nw=expression[i++]-'0';
            while(i<n&&expression[i]>='0'&&expression[i]<='9')
                nw=nw*10+expression[i++]-'0';
            num.push_back(nw);
            if(i<n) op.push_back(expression[i]);
        }
        n=num.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(n,vector<int>()));
        for(int i=0;i<n;i++)
        {
            dp[i][i].push_back(num[i]);
            for(int j=i-1;j>=0;j--)
                for(int k=i-1;k>=j;k--)
                    for(int x:dp[j][k])
                        for(int y:dp[k+1][i])
                            switch(op[k])
                            {
                                case '+':dp[j][i].push_back(x+y);break;
                                case '-':dp[j][i].push_back(x-y);break;
                                case '*':dp[j][i].push_back(x*y);break;
                            }
        }
        return dp[0][n-1];
    }
};
