There are many repeating subquestions in this recursive method, therefore, we could use dynamic programming to avoid this situation by saving the results for subquestions. Here is the DP solution.
```
class Solution {
    vector<int> computeWithDP(string& str,unordered_map<string,vector<int>>& dpMap){
        int size=str.size();
        vector<int>result;
        for(int i=0;i<size;++i){
            char input=str[i];
            if(input=='+' || input=='-' || input=='*'){

                // Split input string into two parts and solve them recursively
                vector<int> result1, result2;
                string substr=str.substr(0,i);

                // check if dpMap has the result for substr
                if(dpMap.find(substr)!=dpMap.end())
                    result1=dpMap[substr];
                else
                    result1=computeWithDP(substr,dpMap);

                substr=str.substr(i+1);
                if(dpMap.find(substr)!=dpMap.end())
                    result2=dpMap[substr];
                else
                    result2=computeWithDP(substr,dpMap);

                for(auto &x: result1){
                    for(auto &y: result2){
                        if(input=='+') 
                            result.push_back(x+y);
                        else if(input=='-')
                            result.push_back(x-y);
                        else
                            result.push_back(x*y);
                    }
                }
            }
        }
        // if the input string contains only number
        if(result.empty()) 
            result.push_back(std::atoi(str.c_str()));

        // save to dpMap
        return dpMap[str]=result;
    }
public:
    vector<int> diffWaysToCompute(string expression) {
        unordered_map<string, vector<int>> dpMap;
        return computeWithDP(expression,dpMap);
    }
};
