        for i,v in enumerate(exp):
            if v=='*' or v=='+' or v=='-':
                left = self.diffWaysToCompute(exp[:i])
                right = self.diffWaysToCompute(exp[i+1:])

                for i in left:
                    for j in right:
                        if v=='*':
                            res.append(i*j)
                        elif v=='-':
                            res.append(i-j)
                        else:
                            res.append(i+j)

        return res or [int(exp)]

