int expsolve(int x,int y,char c)
{
    if(c=='+')
        return x+y;
    if(c=='-')
        return x-y;
    if(c=='*')
        return x*y;
    return x/y;
}
class Solution {
public:
    vector<int> diffWaysToCompute(string s) {
        string x="0";
        vector<int>v;
        vector<char>v1;
        for(auto it:s)
        {
            if(!(it>='0'&&it<='9'))
            {
                v.push_back(stoi(x));
                v1.push_back(it);
                x="0";
                continue;
            }
            x+=it;
        }
        v.push_back(stoi(x));
        int n=v.size();
        vector<vector<vector<int>>>dp(n,vector<vector<int>>(n));
        for(int k=0;k<n;k++)
        {
            for(int i=0;i+k<n;i++)
            {
                if(k==0)
                    dp[i][i].push_back(v[i]);
                else
                {
                    for(int j=i;j<i+k;j++)
                    {
                        for(auto it:dp[i][j])
                        {
                            for(auto it1:dp[j+1][i+k])
                                dp[i][i+k].push_back(expsolve(it,it1,v1[j]));
                        }
                    }
                }
            }
        }
        return dp[0][n-1];
    }
};
