class Solution {
public:
    vector<int> diffWaysToCompute(string input) {
        return divideConquer(input, 0, input.size() - 1);
    }
    vector<int> divideConquer(const string& input, int left, int right)
    {
        vector<int> res;
        for (int i = left; i <= right; ++i) {
            if (input[i] == '+' || input[i] == '-' || input[i] == '*') {
                vector<int> leftVec =  divideConquer(input, left, i - 1);
                vector<int> rightVec =  divideConquer(input, i + 1, right);
                for (int j = 0; j < leftVec.size(); ++j) {
                    for (int k = 0; k < rightVec.size(); ++k) {
                        res.push_back(help(leftVec[j], rightVec[k], input[i]));
                    }
                }
            }
        }
        if (res.size() == 0) {
            res.push_back(stoi(input.substr(left, right - left + 1)));
        }
        return res;
    }
    int help(int l, int r, char op)
    {
        int ret = 0;

        switch(op) {
            case '+':
                ret = l + r;
                break;
            case '-':
                ret = l - r;
                break;
            case '*':
                ret = l * r;
                break;
        }
        return ret;
    }
};
