class Solution:

    OP = {'*':mul,'+':add,'-':sub}
    op_set = set(['+','*','-'])

    def diffWaysToCompute(self, S: str) -> List[int]:
        if S.isdigit():
            return [int(S)]
        else:
            res = []
            for i,s in enumerate(S):
                if s in Solution.op_set:
                    L = self.diffWaysToCompute(S[:i])
                    R = self.diffWaysToCompute(S[i+1:])
                    for l in L:
                        for r in R:
                            res.append(Solution.OP[s](l,r))
            return res                           
