class Solution {
public:
    vector<int> dfs(int l, int r, string s) {
        vector<int> res;
        for (int i = l; i <= r; i++) {
            if (isdigit(s[i]))
                continue;
            vector<int> left = dfs(l, i - 1, s);
            vector<int> right = dfs(i + 1, r, s);
            for (auto& ln : left) {
                for (auto& rn: right) {
                    switch (s[i]) {
                        case '+':
                            res.emplace_back(ln + rn);
                            break;
                        case '-':
                            res.emplace_back(ln - rn);
                            break;
                        case '*':
                            res.emplace_back(ln * rn);
                            break;
                    }
                }
            }
        }

        if (res.size() == 0) {
            int cus = 0;
            for (int i = l; i <= r; i++)
                cus = cus * 10 + s[i] - '0';
            res.emplace_back(cus);
        }

        return res;
    }

    vector<int> diffWaysToCompute(string expression) {
        return dfs(0, expression.size() - 1, expression);
    }
};
