func diffWaysToCompute(expression string) []int {
    var isOperator func(byte) bool 
    isOperator = func(str byte) bool {
        return str == '+' || str == '-' || str == '*'
    }

    var containAnyOperator func(string) bool
    containAnyOperator = func(ex string) bool {
        for i := 0; i < len(ex); i++ {
            if isOperator(ex[i]) {
                return true
            }
        }

        return false
    }

    var toInt func(string) int
    toInt = func(str string) int {
        val, err := strconv.Atoi(str)

        if err != nil {
            panic(err)
        }

        return val
    }

    var dfs func(string) []int
    dfs = func(expression string) []int {
        if len(expression) == 0 {
            return []int{}
        }

        if !containAnyOperator(expression) {
            return []int{ toInt(expression) }
        }

        var result []int

        for i := 0; i < len(expression); i++ {
            if isOperator(expression[i]) {
                leftPart := dfs(expression[:i])
                rightPart := dfs(expression[i+1:])

                for _, leftVal := range leftPart {
                    for _, rightVal := range rightPart {
                        if expression[i] == '+' {
                            result = append(result, leftVal + rightVal)
                        } else if expression[i] == '-' {
                            result = append(result, leftVal - rightVal)
                        } else {
                            result = append(result, leftVal * rightVal)
                        }
                    }
                }
            }
        }  

        return result
    }

    return dfs(expression)
}

