    {
        if(start > end)
            return {};

        string key = to_string(start) + "," + to_string(end);

        if(dp.count(key))
        {
            return dp[key];
        }

        if(end - start <= 1)
        {
            string num = expression.substr(start, end + 1);

            return dp[key] = {stoi(num)};
        }

        vector<int> left;

        vector<int> right;

        vector<int> curr;

        for(int k = start; k <= end; k++)
        {
            if(expression[k] == '+' || expression[k] == '-' || expression[k] == '*')
            {
                left = helper(expression, start, k - 1);

                right = helper(expression, k + 1, end);

                for(auto l : left)
