class Solution {
public:
    vector<int> ans;
    vector<int> n; // number
    vector<char> c; //  + - *

    vector<int> diffWaysToCompute(string e) {
        int csize = e.size();
        int num = 0;

        for(int i = 0; i < csize; ++i) {
            if(e[i] >= '0' && e[i] <= '9') {
                num *= 10;
                num += e[i] - '0';
            } else {
                n.push_back(num);
                num = 0;
                c.push_back(e[i]);
            }
        }
        n.push_back(num);

        int size = n.size();

        // dp[a][b] means what is the result vector for #a to #b 
        vector<vector<vector<int>>> dp(size, vector<vector<int>>(size, vector<int>()));

        for(int i = 0; i < size; ++i) {
            dp[i][i].push_back(n[i]); // add the number itself
        }

        helper(dp, 0, size - 1);

        return dp[0][size - 1];
    }

    void helper(vector<vector<vector<int>>>& dp, int a, int b) {
        if(dp[a][b].size() != 0) {
            return;
        }

        for(int i = a; i < b; ++i) {
            helper(dp, a, i);
            helper(dp, i + 1, b);
        }

        for(int i = a; i < b; ++i) {
            for(int& t1 : dp[a][i]) {
                for(int& t2 : dp[i + 1][b]) {
                    // combine each result in two vectors
                    if(c[i] == '+') {
                        dp[a][b].push_back(t1 + t2);
                    } else if(c[i] == '-') {
                        dp[a][b].push_back(t1 - t2);
                    } else if(c[i] == '*') {
                        dp[a][b].push_back(t1 * t2);
                    }
                }
            }
        }   
    }

};
