First generate all possibilities of parenthesis around given expression using concept of generating all parenthesis,

check out that here :
https://leetcode.com/problems/generate-parentheses/discuss/1804825/c-using-generating-unique-bsts-concept

Then evaluate inorder expression using Stacks:

**Solution** :

```
class Solution {
public:
    vector<int> diffWaysToCompute(string exp) {
        vector<string> exp_v ;
        int j = 0;
        while(j<exp.length()){
            if(exp[j]>='0' && exp[j]<='9') {
                int a = 0,x=1;
                while(exp[j]>='0' && exp[j]<='9'){
                    a=a*x+(exp[j]-'0');x=x*10;
                    j++;
                }
                exp_v.push_back(to_string(a));
            }else{
                exp_v.push_back(checkChar(exp[j]));
                j++;
            }
        }
        vector<string > vs=  help (exp_v,0,exp_v.size()-1);
        // for (auto i:exp_v )cout<< i<<endl;
        vector<int> v ;
        for (auto i: vs ){
            vector<string> exp1 ;
            int j =0;
            while(j<i.length()){
                if(i[j]>='0' && i[j]<='9') {
                    int a = 0,x=1;
                    while(i[j]>='0' && i[j]<='9'){
                        a=a*x+(i[j]-'0');
                        x=x*10;
                        j++;
                    }
                    exp1.push_back(to_string(a));
                }else{
                    exp1.push_back(checkChar(i[j]));
                    j++;
                }
            }
            v.push_back(eval(exp1));
        }
        return v;
    }
    // start=0 and end =n-1
    // counts all possibilites of parenthesis and generates them
    vector<string> help(vector<string> exp,int start,int end){
        if (start>end) return {""};
        if (start==end){
            return {exp[start]};
        }
        vector<string> ans ;
        for (int i =start+1;i<=end;i+=2){
            vector<string> in = help(exp,start,i-1);
            vector<string> out = help(exp,i+1,end);
            for (auto l: in){
                for (auto r:out){
                    ans.push_back("("+l+exp[i]+r+")");
                }
            }
        }
        return ans ;
    } 

    // evaluating my expr
    int eval (vector<string> exp){
        stack <string> st ;
        for (int i = 0;i<exp.size();i++){
            if(exp[i]==")"){
                int b = stoi(st.top());
                st.pop();
                string ops = st.top();
                char op = ops[0];
                st.pop();
                int a = stoi(st.top());
                st.pop();
                st.pop(); // poping out "("
                int c = opEval(a,op,b);
                st.push(to_string(c));
            }else{
                st.push(exp[i]);
            }
        }
        return stoi(st.top());
    }

    string checkChar(char c){
        if (c=='+') return "+";
        if (c=='-') return "-";
        if (c=='*') return "*";
        if (c==')') return ")";
        return "(";
    }

    int opEval(int a, char op , int b){
        if (op=='+') return a+b;
        else if (op=='-') return a-b;
        else return a*b;
    }
};
