import java.util.*;
class Solution {
    String data[];

    List<Integer> dp[][];
    public List<Integer> diffWaysToCompute(String expression) {

        StringTokenizer token=new StringTokenizer(expression,"+-*",true);
        data=new String[token.countTokens()];
        int i=0;
        while(token.hasMoreTokens())
            data[i++]=token.nextToken();

        dp=new ArrayList[data.length][data.length];
        return rec(0,data.length-1);
    }
    List<Integer> rec(int i,int j){

        if(dp[i][j]!=null) return dp[i][j];

        ArrayList<Integer> result=new ArrayList<>();
        if(i==j) {
            result.add(Integer.parseInt(data[i]));
            return dp[i][j]=result;
        } 
        for(int k=i+1;k<=j-1;k+=2){
            List<Integer>  a = rec(i,k-1);
            List<Integer> b = rec(k+1,j);
            for(int a1:a){
                for(int b1:b)
                    result.add(cal(data[k],a1,b1));
            }
        }
        return  dp[i][j]=result;
    }

    int cal(String op,int a,int b){

        switch(op){
                case "+" :return a+b;
                case "-": return a-b;
                case "*": return a*b;
        }
        return 0;
    }   
}
