        for(int i = 0; i < n; i++){
            if(expression[i] >= '0' && expression[i] <= '9'){
                num = 10*num + (expression[i]-'0');
            }
            else{
                operands.push_back(num);
                num = 0;
                operators.push_back(expression[i]);
            }
        }
        operands.push_back(num);
        return findpossibleSol(operands, operators);
    }
};
