struct HashPair {
public:
    size_t operator() (const pair<int, int> &p) const {
        size_t h1 = hash<int>{}(p.first);
        size_t h2 = hash<int>{}(p.second);
        return h1 ^ h2;
    }
};

class Solution {
private:
    unordered_map<pair<int, int>, vector<int>, HashPair> memo;

    int str2Val(string &str, int s, int e) {
        int mul = 1;
        int res = 0;
        for (int i = e; i >= s; i--) {
            res += (str[i] - '0') * mul;
            mul *= 10;
        }
        return res;
    }

    bool isOperator(char c) {
        if (c == '+' || c == '-' || c == '*') return true;
        return false;
    }

    int cal(int a, int b, char op) {
        switch(op) {
            case '+':
                return a + b;
            case '-':
                return a - b;
            case '*':
                return a * b;
            default:
                break;
        }
        return 0;
    }

    vector<int> dfs(string &exp, int s, int e) {
        vector<int> res;

        // End case, no operator in this string
        if (e - s < 2) {
            res.push_back(str2Val(exp, s, e));
            return res;
        }

        // We have results for this particular interval
        if (memo.count({s, e})) return memo[{s, e}];

        // For each operator in current interval, recursively calculate result
        for (int i = s; i <= e; i++) {
            if (!isOperator(exp[i])) continue;

            vector<int> l = dfs(exp, s, i - 1);
            vector<int> r = dfs(exp, i + 1, e);

            for (int j = 0; j < l.size(); j++) {
                for (int k = 0; k < r.size(); k++) {
                    res.push_back(cal(l[j], r[k], exp[i]));
                }
            }
        }

        // Push result to memo to eliminate repeated computation
        memo[{s, e}] = res;
        return res;
    }
public:
    vector<int> diffWaysToCompute(string exp) {
        return dfs(exp, 0, exp.size() - 1);
    }
};
