    public List<Integer> diffWaysToCompute(String string) {
        List<Integer> ans=new ArrayList<>();

        for(int i=0;i<string.length();i++){
            char x=string.charAt(i);
            if(x=='+' || x=='-' || x=='*'){
                String a=string.substring(0,i);
