class Solution {
    /* Hash map to use as a cache */
    unordered_map<string, vector<int>> mem;

    /* Given an expression, return the
    indexes of the operands */
    vector<int> getOps(string str)
    {
        vector<int> ops;

        for(int i=0; i<str.length(); i++)
        {
            if((str[i] == '+') || (str[i] == '-') ||
               (str[i] == '*'))
            {
                ops.push_back(i);
            }
        }

        return ops;
    }

    /* Compute the given operation on the
    given integers */
    int compute(int num1, int num2, char op)
    {
        switch(op)
        {
            case '+':
                return (num1 + num2);
            case '-':
                return (num1 - num2);
            default:
                return (num1 * num2);
        }
    }

public:
    vector<int> diffWaysToCompute(string expression) {
        vector<int> ret;

        /* Get indexes of operators */
        vector<int> ops = getOps(expression);

        if(expression.length() == 0)
            return ret;

        /* Must be an integer value if no operators 
        return that value */
        if(ops.size() == 0)
        {
            int num = stoi(expression, nullptr);
            ret.push_back(num);
            return ret;
        }

        /* Return ways if already computed */
        if(mem.count(expression))
            return mem[expression];

        for(int i=0; i<ops.size(); i++)
        {

            /* split string at operand */
            string exp1 = expression.substr(0,ops[i]);
            string exp2 = expression.substr(ops[i]+1, expression.size() - (ops[i] - 1));

            vector<int> res1 = diffWaysToCompute(exp1);
            vector<int> res2 = diffWaysToCompute(exp2);

            for(int k=0; k<res1.size(); k++)
            {
                for(int j=0; j<res2.size(); j++)
                {
                    int total = compute(res1[k], res2[j], expression[ops[i]]);
                    ret.push_back(total);
                }
            }
        }

        /* cache computed ways */
        mem.insert({{expression, ret}});
        return ret;
    }
};
