var diffWaysToCompute = function(expression) {
    const numsArr = expression.match(/\d+/g).map(Number);
    const opsArr = expression.match(/[^\d]/g);

    function backtrack(nums, ops) {
        if(nums.length == 1) return nums;
        const result = [];

        for(let i = 0; i < ops.length; i++) {
            const left = backtrack(nums.slice(0, i+1), ops.slice(0, i));
            const right = backtrack(nums.slice(i+1), ops.slice(i+1));

            for(let n1 of left) {
                for(let n2 of right) {
                    if(ops[i] == '*') result.push(n1 * n2);
                    else if(ops[i] == '+') result.push(n1 + n2);
                    else result.push(n1 - n2)
                }
            }
        }
        return result;
    }
    return backtrack(numsArr, opsArr);
};
