class Solution {
    ArrayList<Integer> numbers;
    ArrayList<Character> operators; 
    ArrayList<Integer>[][] dp;
    int n;

    public List<Integer> diffWaysToCompute(String input) {
        //intialise 
        numbers = new ArrayList();
        operators = new ArrayList();

        splitNumbersAndOperators(input);

        n = numbers.size();
        dp = new ArrayList[n][n];

        //find all ways to compute         
        findWays();

        //all possible ways to compute 
        return dp[0][n - 1];
    }

    private void findWays(){

        //iterate for each widows size [1 to n]
        for(int windowSize = 1; windowSize <= n; windowSize++){
            for(int left = 0; left <= n - windowSize; left++){

                int right = left + windowSize - 1;

                //save all the posible result from left to right, [left, right] e,g inclusive
                dp[left][right] = getAllPossibleResult(left, right);
            }
        }

    }

    private ArrayList<Integer> getAllPossibleResult(int left, int right){
        ArrayList<Integer> results = new ArrayList();

        if(left == right){
            results.add(numbers.get(left));
            return results;
        } 

        //    2  *  3  -  4  *  5 - 9

        //    0     1     2     3   4  -> index       
        //    2     3     4     5   9  -> numbers
        //    *     -     *     -      -> operators
        //.               oI           -> operatorIndex = 2

        // possible left calculation
        //     2     3    4 
        //     *     - 
        //     1. (2 * 3) - 4 =  2
        //     2. 2 * (3 - 4) =  -2
        //     dp[left][operatorIndex] = [2, -2]
        //     dp[0][2] = [2, -2]


        // possible right calculation
        //     5     9
        //     -
        //     1. (5 - 9) = -4
        //     dp[operatorIndex + 1][right] = [-4]
        //     dp[2 + 1][4] = [-4]


        for(int operatorIndex = left; operatorIndex < right; operatorIndex++){
            char operator = operators.get(operatorIndex);

            //get all possible left calculation result from left -> operatorIndex
            for(int leftResult :  dp[left][operatorIndex]){

                //get all possible right calculation result from operatorIndex + 1 -> right
                for(int rightResult :  dp[operatorIndex + 1][right]){
                    //add calculation result
                    results.add(calculate(leftResult, rightResult,  operator));
                }
            }
        }

        return results;
    }

    private void splitNumbersAndOperators(String str){
        // 2*32-1
        // 012345

        int num = 0;

        for(char c : str.toCharArray()){
            if(c == '+' || c == '-' || c == '*') {
                //add this oerator in the operator list
                operators.add(c);

                //add number in number list
                numbers.add(num);

                //now reset this numbere to 0,
                //so it can find another number after this operator e.g c
                num = 0;
            } else {
                int digit = c - '0';
                num = (num * 10) + digit;
            }
        }

        //add last number
        numbers.add(num);
    }

    private int calculate(int num1, int num2, char operator){
        if(operator == '+') return num1 + num2;

        if(operator == '-') return num1 - num2;

        if(operator == '*') return num1 * num2;

        return  num1 * num2;
    }
}

