class Solution:


    def diffWaysToCompute(self, input: str) -> List[int]:

        memo = {}

        def calc(input):

		    #memoziation
            if input in memo:
                return memo[input]
            ops = {
                '+': lambda x,y: x + y,
                '-': lambda x,y: x - y,
                '*': lambda x,y: x * y
            }

            res = []
            for i in range(len(input)):
                if input[i] in ops:

                    # split array into 2 parts for each operator
                    pre = calc(input[:i])
                    post = calc(input[i+1:])

                    #recurively calculate boht sides. 
                    # This is the same is (pre) op (post)
                    for j in range(len(pre)):
                        for k in range(len(post)):
                            value = ops[input[i]](int(pre[j]),int(post[k]))
                            res.append(value)

            # if there were non operators, then just return the numbers
            if len(res) == 0:
                res.append(int(input))

			#memoziation - save it for later
            memo[input] = res
            return res

        return calc(input)
