class Solution {
public:
    vector<int> diffWaysToCompute(string input) {
        input.push_back('#');
		/** Values contain all the operands
		  * Operators contains all the operators
		  * dp[{l, r}] contains all possible results for subarray [l, r] in 'values' 
		  */
        vector<int> values, operators;
		map<pair<int, int>, vector<int>> dp;
        int start = 0;
        for (int i = 0; i < (int)input.length(); i++) {
            if (!(input[i] >= '0' && input[i] <= '9')) {
                int cur = 0;
                for (int j = start; j < i; j++)
                    cur = cur * 10 + (input[j] - '0');
                start = i + 1;
                values.push_back(cur);
                if (!(input[i] == '#')) {
                    if (input[i] == '+')
                        operators.push_back(0);
                    else if (input[i] == '-')
                        operators.push_back(1);
                    else operators.push_back(2);
                }
            }
        }
        for (int i = 0; i < (int)values.size(); i++) {
            vector<int> v;
            v.push_back(values[i]);
            dp[{i, i}] = v;
        }
        for (int l = 2; l <= (int)values.size(); l++) {
            for (int i = 0; i < (int)values.size() - l + 1; i++) {
                int j = i + l - 1;
                vector<int> v;
                for (int k = i + 1; k <= j; k++) {
                    int op = operators[k - 1];
                    for (auto it1 : dp[{i, k - 1}]) {
                        for (auto it2 : dp[{k, j}]) {
                            if (op == 0)
                                v.push_back(it1 + it2);
                            else if (op == 1)
                                v.push_back(it1 - it2);
                            else v.push_back(it1 * it2);
                        }
                    }
                }
                dp[{i, j}] = v;
            }
        }
        return dp[{0, (int)values.size() - 1}];
    }
};
