    public boolean hasNextOperator(String input, int start, int end) {
        for (int i = start; i <= end; i++) 
            if (!Character.isDigit(input.charAt(i))) return true;
        return false;
    }

    public int calculate (int a, int b, char c) {
        int result = 0;
        if (c == '+') 
            result = a + b;
        else if (c == '-') 
            result = a - b;
        else
            result = a * b;
        return result;
