    public List<Integer> recur(String input, int low, int high) {
        List<Integer> result = new ArrayList<>();
        // base case : one integer
        if (!hasNextOperator(input, low, high)) {
            int num = 0;
            for (int i = low; i <= high; i++) 
                num = num * 10 + (input.charAt(i) - '0');
            result.add(num);
            return result;
        }

        int start = low;
        do {
            List<Integer> list = new ArrayList<>();
            int index = findNextOperator(input, start, high);
            // divide and conquer the subproblem
            List<Integer> left = new ArrayList<>();
            List<Integer> right = new ArrayList<>();
            left = recur (input, low, index-1);
            right = recur (input, index + 1, high);

            // combine the solutions to the subproblem
            for (int i = 0; i < left.size(); i++) {
                for (int j = 0; j < right.size(); j++) {
                    int cur = calculate (left.get(i), right.get(j), input.charAt(index));
                    list.add(cur);
