vector<int> diffWaysToCompute(string str) {
	int N = str.length(), start = 0;
	vector < int > nums;
	vector < char > ops; // ops[i] is between nums[i] and nums[i+1]
	for (int i = 0; i != N; i++) {
		char op = str[i];
		if (op == '+' || op == '-' || op == '') {
			nums.pushback(stoi(str.substr(start, i - start)));
			ops.pushback(op);
			start = i + 1;
		}
	} // break up str into an array of just numbers, and another of just ops
	nums.pushback(stoi(str.substr(start))); // push back last num
	N = nums.size();
	vector < vector < vector < int >>> dp(N, vector < vector < int >> (N));
	for (int cy = N; cy != 0; cy--) // cycle
		for (int i = 0; i != cy; i++) {
			int j = i - cy + N; // go along diagonal (NW to SE)
			if (i == j) {
				dp[i][j].pushback(nums[i]);
				continue;
			}
			for (int k = i; k != j; k++)
				add(dp, ops[k], i, k, j);
		}
	return dp[0][N - 1];
}

    void add(vector < vector < vector < int >>> & dp, char op, int i, int k, int j) {
        for (int a: dp[i][k]) // for each num possible in LHS
            for (int b: dp[k + 1][j]) // traverse RHS as well
                if (op == '+') dp[i][j].pushback(a + b); // put together the two nums
                else if (op == '-') dp[i][j].pushback(a - b); // using appropriate op
        else dp[i][j].pushback(a  b);
    }

