func parseString(input string) ([]int, []byte) {
	nums := make([]int, 0)
	ops := make([]byte, 0)
	num := 0
	for i := 0; i < len(input); i++ {
		ch := input[i]
		if ch >= '0' && ch <= '9' {
			num = num*10 + int(ch-'0')
		} else {
			nums = append(nums, num)
			num = 0
			ops = append(ops, ch)
		}
	}
	nums = append(nums, num)
	return nums, ops
}

func combine(op byte, a, b int) int {
	switch op {
	case '*':
		return a * b
	case '-':
		return a - b
	case '+':
		return a + b
	}
	return 0
}

func merge(op byte, a []int, b []int, c *[]int) {
	for _, k1 := range a {
		for _, k2 := range b {
			k := combine(op, k1, k2)
			*c = append(*c, k)
		}
	}
}

func diffWaysToCompute(input string) []int {
	nums, ops := parseString(input)
	size := len(nums)
	dp := make([][][]int, size)

	for i := 0; i < size; i++ {
		dp[i] = make([][]int, size)
		for j := 0; j < size; j++ {
			dp[i][j] = make([]int, 0)
		}
		dp[i][i] = append(dp[i][i], nums[i])
	}

	for l := 2; l <= size; l++ {
		for i := 0; i < size-l+1; i++ {
			j := i + l - 1
			for k := i; k < j; k++ {
				merge(ops[k], dp[i][k], dp[k+1][j], &dp[i][j])
			}
		}
	}
	return dp[0][size-1]
}
