func diffWaysToCompute(input string) []int {
	nums, ops := parseString(input)
	size := len(nums)
	dp := make([][][]int, size)

	for i := 0; i < size; i++ {
		dp[i] = make([][]int, size)
		for j := 0; j < size; j++ {
			dp[i][j] = make([]int, 0)
		}
		dp[i][i] = append(dp[i][i], nums[i])
	}

	for l := 2; l <= size; l++ {
		for i := 0; i < size-l+1; i++ {
			j := i + l - 1
			for k := i; k < j; k++ {
				merge(ops[k], dp[i][k], dp[k+1][j], &dp[i][j])
			}
		}
	}
	return dp[0][size-1]
}
