```
class Solution {
    Map<String, List<Integer>> cache = new HashMap<>();
    public List<Integer> diffWaysToCompute(String input) {
        List<Integer> ret = new ArrayList<>();
        if (input == null) return ret;
        if (cache.containsKey(input)) return cache.get(input);
        boolean isAllDigit = true;
        for (char c: input.toCharArray()) {
            if (c == '+' || c == '-' || c == '*') { 
                isAllDigit = false;
                break;
            }
        }
        if (isAllDigit) return Arrays.asList(Integer.valueOf(input));

        for (int i = 1; i < input.length()-1; ++i) {
            char c = input.charAt(i);
            if (c == '+' || c == '-' || c == '*') {
                List<Integer> lList = diffWaysToCompute(input.substring(0, i));
                List<Integer> rList = diffWaysToCompute(input.substring(i+1));
                for (Integer lNum: lList) {
                    for (Integer rNum: rList) {
                        switch (c) {
                            case '+': ret.add(lNum + rNum); break;
                            case '-': ret.add(lNum - rNum); break;
                            case '*': ret.add(lNum * rNum); break;
                            default: break;
                        }
                    }
                }
            }
        }
        cache.put(input, ret);
        return ret;
    }
}
