        while(stack.Count > 0)
        {
            if(stack.Peek().MoveNext())
            {
                if(stack.Peek().Current.IsInteger())
                    return true;
                else
                    stack.Push(stack.Peek().Current.GetList().GetEnumerator());
            }
            else
                stack.Pop();
        }

        return false;
    }

    public int Next() {

        return stack.Peek().Current.GetInteger();
    }
}
```

