            next = null;
            while (next == null && index < list.size()) {
                if (list.get(index).isInteger()) {
                    next = list.get(index).getInteger();
                    index++;
                } else {
                    if (nextIterator == null) {
                        nextIterator = new NestedIterator(list.get(index).getList());
