public class NestedIterator implements Iterator<Integer> {
    List<NestedInteger> list;
    int                 pos;
    NestedIterator      nested;
    Integer             cur;

    public NestedIterator(List<NestedInteger> list) {
        this.list = list;
    }

    @Override
    public Integer next() {
        if (nested != null) {
            return nested.next();
        }
        Integer v = cur;
        cur       = null;
        return v;
    }

    @Override
    public boolean hasNext() {
        if (cur != null) return true;

        if (this.nested != null) {
            if (this.nested.hasNext()) 
                return true;
            this.nested = null;
        }

        if (pos >= list.size()) 
            return false;

        NestedInteger n = list.get(pos++);

        if (!n.isInteger()) {
            nested = new NestedIterator(n.getList());
            return hasNext();
        }

        cur = n.getInteger();
        return true;
    }
}
