public class NestedIterator implements Iterator<Integer> {

	private List<Integer> flattened = new ArrayList<>();
	private int index = 0;

	public NestedIterator(List<NestedInteger> nestedList) {
		if (nestedList == null || nestedList.size() == 0) return; // throw exception

		flattened = new ArrayList<>();
		dfs(nestedList, flattened);
	}

	private void dfs(List<NestedInteger> nestedList, List<Integer> flattened) {
		for (int i = 0; i < nestedList.size(); i++) {
			NestedInteger value = nestedList.get(i);
			if (value.isInteger()) 
				flattened.add(value.getInteger());
			else
				dfs(value.getList(), flattened);
		}
	}

	@Override
	public Integer next() {
		return index < flattened.size() ? flattened.get(index++) : null;
	}

	@Override
	public boolean hasNext() {
		return index < flattened.size();
	}
}

