class NestedIterator {
public:
    NestedIterator(vector<NestedInteger> &nestedList) : list_(nestedList) {
    }

    int next() {
        auto& ni = list_[index_];
        if (ni.isInteger()) {
            int v = ni.getInteger();
            index_ ++;
            return v;
        } else {
            return nestedIter_->next();
        }
    }

    bool hasNext() {
        if (index_ >= list_.size())
            return false;

        auto& current = list_[index_]; 
        if (current.isInteger())
            return true;

        if (!nestedIter_ && !current.getList().empty())
            nestedIter_.reset(new NestedIterator(current.getList()));

        if (nestedIter_ && nestedIter_->hasNext())
            return true;
        else    
            nestedIter_ = nullptr;

        // current list is empty, check remain nodes.
        index_ += 1;
        return hasNext();
    }

private:
    vector<NestedInteger> &list_;    // avoid copy
    int index_{0};

    // It's valid if list_[index] is a list
    std::unique_ptr<NestedIterator> nestedIter_;
};

