class NestedIterator {
public:

    queue<int>q;

    void findAllValue(NestedInteger x) {
        if (x.isInteger()) {
            q.push(x.getInteger());
        }
        else {
            for (auto y : x.getList())
                findAllValue(y);
        }
    }

    NestedIterator(vector<NestedInteger> &nestedList) {
        for(auto x:nestedList){
            findAllValue(x);
        }    
    }

    int next() {
        int x;
        if(hasNext()){
            x=q.front();
            q.pop();
        }
         return x;
    }

    bool hasNext() {
        if(!q.empty())return 1;
        return 0;
    }
};

