public class NestedIterator {
    private IEnumerator<int> iterator;

    /// O(D) - D is # of nesting for given list
    public NestedIterator(IList<NestedInteger> nestedList) {
        this.iterator = FlattenNestedList(nestedList).GetEnumerator();
    }

    // O(1)
    public bool HasNext() {
        return this.iterator.MoveNext();
    }

    // O(1)
    public int Next() {
        return this.iterator.Current;
    }

    // O(D)
    private IEnumerable<int> FlattenNestedList(IList<NestedInteger> nestedList) {
     //   if (nestedList is null || nestedList.Count < 1) yield return int.MinValue;

        foreach (var n in nestedList) {
            if (n.IsInteger())
                yield return n.GetInteger();
            else
                foreach (var e in FlattenNestedList(n.GetList()))
                    yield return e;
        }
    }
}
