										// the top of stack item is an integer, 
										// we can directly reture. Here we
										// have an assumption here, the caller
										// always call hasNext first, before call next().
        stk.pop();
        return cur.getInteger();
    }

    bool hasNext() {
        while(!stk.empty()) {
            NestedInteger cur = stk.top();            

            if (cur.isInteger()) {
                return true;
            } else {
                auto cc = cur.getList();
                stk.pop();

                for(int i = cc.size() - 1; i >= 0; --i) {
                    stk.push(cc[i]);
                }
            }
        }

        return false;
    }
