#define stV (*(st.top().first))
#define stI (st.top().second)

class NestedIterator {
private:
	// Each entry in stack keeps a pointer to vector and the current index it is at in the corresponding vector
    stack <pair<vector<NestedInteger>*, int>> st;
    void getNextAtTop() {
        while(!st.empty()){
            if (stI<stV.size() && stV[stI].isInteger())
                break;
            if (stI<stV.size() && !stV[stI].getList().empty())
                st.push({&(stV[stI].getList()), 0});
            else if(stI < (int)stV.size()-1)
                stI++;
            else{
                st.pop();
                if(!st.empty()) stI++;
            }
        }
    }
public:
    NestedIterator(vector<NestedInteger> &nestedList) {
        if (nestedList.empty()) return;
        st.push({&nestedList, 0});
        getNextAtTop();
    }

    int next() {
        if (st.empty()) return -1;
        int ans = stV[stI].getInteger();
        while(!st.empty() && stI>=stV.size()-1){
            st.pop();
        }
        if (st.empty()) return ans;
        stI++;
        getNextAtTop();
        return ans;
    }

    bool hasNext() {
        return !st.empty();
    }
};
