class NestedIterator {
public:
    NestedIterator(vector<NestedInteger> &nestedList) {

        for ( auto& elem : nestedList )
            tearDown( elem );
    }

    int next() {

        int top = queue_.front();
        queue_.pop();   
        return top;
    }

    bool hasNext() {

        return !queue_.empty();

    }

    void tearDown( NestedInteger& elem )      
    {
        if ( elem.isInteger() )
             queue_.push( elem.getInteger() );
        else
        {
            for ( auto& lists : elem.getList())
                  tearDown( lists );
        }
    }

    std::queue<int> queue_;
};
