//now our only hope left is greedy solution that will run in O(n) time complexity and
//O(1) space complexity

class Solution {
public:
    int jump(vector<int>& nums) {

        int n = nums.size();
        if(n == 0){
            return 1;
        }

        int maxReach = nums[0];
        int jump = 1;
        int step = nums[0];

        for(int i=1;i<n;i++){

            if(i == n-1){
                return jump;
            }
            step--;
            maxReach = max(maxReach,i+nums[i]);

            if(step == 0){
                if(i >= maxReach){
                    return -1;
                }
                jump++;
                step = maxReach - i;
            }

        }

        return 0;
    }
};
```

