class Solution {
public:
    int jump(vector<int>& nums)
    {
        int n=nums.size();
        int jump=0;

        int maximum_reach=0;
        int max_pos=0;

        for(int i=0;i<n;i++)
        {
            if(i==n-1) return jump;
            maximum_reach = max(maximum_reach,i+nums[i]);      // for each particular idx storing the max jump

            // If i has reached to it's max position then we need to jump to go on next
            if(i==max_pos)
            {
                jump++;                          // increase jump
                max_pos = maximum_reach;         // assign max_reach as a max_pos for this jump
            }
        }
        return jump;
    }
};
```

