class Solution {
    public int jump(int[] nums) {
        int res = 0;
        int l, r, farthest;
        l = r = farthest = 0;
        while(r < nums.length - 1){
            farthest = 0;
			// r+1 because we also need to include position r
            for(int i=l; i<r+1; i++){
                farthest = Math.max(farthest, i + nums[i]);
            }
            l = r+1;
            r = farthest;
            res++;
        }
        return res;
    }
}
