✔✔  **DYNAMIC PROGRAMMING**

class Solution {
public:

	 int solve(int index,vector<int>& nums,vector<int>&dp)
		{

	   if(index>=nums.size()-1) //base condition if we reached at last index or beyond it 
        {
            return 0;
        }
        if(nums[index]==0) // if index element is zero then it is not possible to move further  return ing  large value 
        {
            return 1e5;
        }
        if(dp[index]!=-1)
        {
            return dp[index];
        }
        int ans=1e5;
        for(int i=1;i<=nums[index];++i)// trying all possible ways 
        {
            ans=min(ans,1+solve(index+i,nums,dp)); //taking min ans
        }
        return dp[index]=ans;
    }
    int jump(vector<int>& nums) {
        vector<int>dp(nums.size(),-1); //dp array
        return solve(0,nums,dp);
    }
};
